mapper.select:
SELECT * FROM user
WHERE job_num = 1
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE user
SET job_num = 0
WHERE user_id = #{user_id}

mapper.select:
SELECT * FROM user WHERE job_num = 1
AND ( user_id LIKE CONCAT('%', #{keyword}, '%') OR user_name LIKE CONCAT('%', #{keyword}, '%') )
LIMIT #{pageSize} OFFSET #{offset}
