mapper.delete:
UPDATE user
SET delete_plug = true ,delete_user_date = now(),delete_content= #{delete_content} 
WHERE user_id = #{user_id} AND password = #{password}

mapper.select:
SELECT * FROM user
WHERE job_num = 0
LIMIT #{pageSize} OFFSET #{offset}

SELECT * FROM user WHERE job_num = 0
AND ( user_id LIKE CONCAT('%', #{keyword}, '%') OR user_name LIKE CONCAT('%', #{keyword}, '%') OR mail LIKE CONCAT('%', #{keyword}, '%') OR adress LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%') )
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE user
SET job_num = 1
WHERE user_id = #{user_id}
